css:
.rpg-inventory {
  width: 400px;
  background: linear-gradient(135deg, #1a1a1a 0%, #2d2d2d 100%);
  border: 2px solid #8b7355;
  border-radius: 8px;
  padding: 20px;
  color: #f0e6d2;
  font-family: 'Cinzel', serif;
}
.inventory-header {
  display: flex;
  justify-content: space-between;
  margin-bottom: 15px;
  border-bottom: 1px solid #8b7355;
  padding-bottom: 10px;
}
.inventory-grid {
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 8px;
}
.item-slot {
  width: 60px;
  height: 60px;
  background: #0f0f0f;
  border: 2px solid #4a4a4a;
  border-radius: 4px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.2s;
  position: relative;
}
.item-slot:hover {
  border-color: #c9aa71;
  transform: scale(1.05);
}
.item-slot.filled {
  background: linear-gradient(135deg, #2a2a2a, #1a1a1a);
  border-color: #8b7355;
}
.item-icon {
  font-size: 32px;
}
.item-count {
  position: absolute;
  bottom: 2px;
  right: 4px;
  font-size: 12px;
  color: #fff;
  text-shadow: 1px 1px 2px #000;
}

