tictactoe.js:
let board = ['', '', '', '', '', '', '', '', ''];
let currentPlayer = 'X';
let gameActive = true;

const winConditions = [
  [0, 1, 2], [3, 4, 5], [6, 7, 8], // Rows
  [0, 3, 6], [1, 4, 7], [2, 5, 8], // Columns
  [0, 4, 8], [2, 4, 6]             // Diagonals
];

function initBoard() {
  const boardEl = document.getElementById('board');
  boardEl.innerHTML = '';

  for (let i = 0; i < 9; i++) {
    const cell = document.createElement('button');
    cell.className = 'cell';
    cell.onclick = () => handleCellClick(i);
    cell.id = `cell-${i}`;
    boardEl.appendChild(cell);
  }

  updateStatus();
}

function handleCellClick(index) {
  if (!gameActive || board[index] !== '') return;

  board[index] = currentPlayer;
  const cell = document.getElementById(`cell-${index}`);
  cell.textContent = currentPlayer;
  cell.classList.add('taken', currentPlayer.toLowerCase());

  if (checkWinner()) {
    document.getElementById('status').textContent = `üéâ –ü–æ–±–µ–¥–∏–ª: ${currentPlayer}`;
    gameActive = false;
    highlightWinner();
    return;
  }

  if (board.every(cell => cell !== '')) {
    document.getElementById('status').textContent = 'ü§ù –ù–∏—á—å—è!';
    gameActive = false;
    return;
  }

  currentPlayer = currentPlayer === 'X' ? 'O' : 'X';
  updateStatus();
}

function checkWinner() {
  return winConditions.some(condition => {
    const [a, b, c] = condition;
    return board[a] && board[a] === board[b] && board[a] === board[c];
  });
}

function highlightWinner() {
  winConditions.forEach(condition => {
    const [a, b, c] = condition;
    if (board[a] && board[a] === board[b] && board[a] === board[c]) {
      [a, b, c].forEach(i => {
        document.getElementById(`cell-${i}`).classList.add('winner');
      });
    }
  });
}

function updateStatus() {
  if (gameActive) {
    document.getElementById('status').textContent = `–•–æ–¥: ${currentPlayer}`;
  }
}

function resetGame() {
  board = ['', '', '', '', '', '', '', '', ''];
  currentPlayer = 'X';
  gameActive = true;
  initBoard();
}

initBoard();
