  for (let i = 0; i < 20; i++) {
    const slot = document.createElement('div');
    slot.className = 'item-slot';

    if (items[i]) {
      slot.classList.add('filled');
      slot.innerHTML = `
        <span class="item-icon">${items[i].icon}</span>
